import styled from 'styled-components';
import { shade } from 'polished'


export const Container = styled.div`
    margin-top: 4rem;

    h1 {
        text-align: center;
    }

    table { 
        width: 100%;
        border-spacing: 0 0.5rem;
    }

    th {
        color: var(--text-body);
        font-weight: 500;
        padding: 1rem 2rem;
        text-align: left;
        line-height: 1.5rem;
    }

    td { 
        padding: 1rem 2rem;
        border: 0;
        background: var(--shape);
        color: var(--text-body);
        border-radius: 0.25rem;

        &:first-child {
            color: var(--text-tittle);
        }
    }

`;
